package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		int num=0;
		if(num%2==0) {
			System.out.println(num + "is a even number");
		}
		else if(num%2!=0) {
			System.out.println(num + "is a odd number");
		}
		else {
			System.out.println(num + "is zero");
		}
		// TODO Auto-generated method stub

	}

}
